// App.js (Frontend)
import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import {
  Container,
  Typography,
  TextField,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  Snackbar,
  Alert,
} from "@mui/material";

const socket = io("http://localhost:5000");

function App() {
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [recipientEmail, setRecipientEmail] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Fetch registered users from the backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/users")
      .then((response) => {
        setUsers(response.data.users);
      })
      .catch((error) => {
        setError("Failed to fetch users");
      });
  }, []);

  // Register user
  const register = async () => {
    if (!email || !mobile) {
      setError("Email and mobile are required");
      return;
    }
    try {
      await axios.post("http://localhost:5000/register", { email, mobile });
      setSuccess("Registration successful");
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data.users);
    } catch (error) {
      setError(error.response?.data?.error || "Registration failed");
    }
  };

  // Login user
  const login = () => {
    if (!email) {
      setError("Email is required");
      return;
    }
    if (users.includes(email)) {
      setLoggedIn(true);
      socket.emit("authenticate", email);
      setSuccess("Login successful");
    } else {
      setError("User not found");
    }
  };

  // Send message
  const sendMessage = () => {
    if (!recipientEmail || !message) {
      setError("Recipient email and message are required");
      return;
    }
    socket.emit("sendMessage", { recipientEmail, senderEmail: email, message });
    setChat([...chat, { senderEmail: email, message, timestamp: new Date() }]);
    setMessage("");
  };

  // Receive messages
  useEffect(() => {
    socket.on("message", (msg) => {
      setChat((prevChat) => [...prevChat, msg]);
    });
  }, []);

  // Close snackbar
  const handleClose = () => {
    setError("");
    setSuccess("");
  };

  return (
    <Container>
      <Typography variant="h3" align="center" gutterBottom>
        P2P Chat Application
      </Typography>

      {!loggedIn ? (
        <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px" }}>
          <Typography variant="h5" gutterBottom>
            Register
          </Typography>
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Mobile"
            fullWidth
            margin="normal"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={register}>
            Register
          </Button>

          <Typography variant="h5" gutterBottom style={{ marginTop: "20px" }}>
            Login
          </Typography>
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={login}>
            Login
          </Button>
        </Paper>
      ) : (
        <Paper elevation={3} style={{ padding: "20px" }}>
          <Typography variant="h5" gutterBottom>
            Chat
          </Typography>
          <TextField
            label="Recipient Email"
            fullWidth
            margin="normal"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
          />
          <TextField
            label="Message"
            fullWidth
            margin="normal"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={sendMessage}>
            Send
          </Button>

          <List style={{ marginTop: "20px" }}>
            {chat.map((msg, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={msg.message}
                  secondary={`From: ${msg.senderEmail} at ${new Date(
                    msg.timestamp
                  ).toLocaleTimeString()}`}
                />
              </ListItem>
            ))}
          </List>
        </Paper>
      )}

      {/* Snackbars for error and success messages */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="error">
          {error}
        </Alert>
      </Snackbar>
      <Snackbar open={!!success} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          {success}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
