// App.js (Frontend)
import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import {
  Container,
  Typography,
  TextField,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  Avatar,
  Divider,
  Badge,
  Snackbar,
  Alert,
  Grid,
  IconButton,
} from "@mui/material";
import { Send as SendIcon } from "@mui/icons-material";

const socket = io("http://localhost:5000");

function App() {
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [recipientEmail, setRecipientEmail] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Fetch registered users from the backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/users")
      .then((response) => {
        setUsers(response.data.users);
      })
      .catch((error) => {
        setError("Failed to fetch users");
      });
  }, []);

  // Register user
  const register = async () => {
    if (!email || !mobile) {
      setError("Email and mobile are required");
      return;
    }
    try {
      await axios.post("http://localhost:5000/register", { email, mobile });
      setSuccess("Registration successful");
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data.users);
    } catch (error) {
      setError(error.response?.data?.error || "Registration failed");
    }
  };

  // Login user
  const login = () => {
    if (!email) {
      setError("Email is required");
      return;
    }
    if (users.includes(email)) {
      setLoggedIn(true);
      socket.emit("authenticate", email);
      setSuccess("Login successful");
    } else {
      setError("User not found");
    }
  };

  // Send message
  const sendMessage = () => {
    if (!recipientEmail || !message) {
      setError("Recipient email and message are required");
      return;
    }
    socket.emit("sendMessage", { recipientEmail, senderEmail: email, message });
    setChat((prevChat) => [
      ...prevChat,
      { senderEmail: email, message, timestamp: new Date() },
    ]);
    setMessage("");
  };

  // Receive messages
  useEffect(() => {
    const handleMessage = (msg) => {
      setChat((prevChat) => [...prevChat, msg]);
    };

    socket.on("message", handleMessage);

    // Cleanup the event listener
    return () => {
      socket.off("message", handleMessage);
    };
  }, []);

  // Close snackbar
  const handleClose = () => {
    setError("");
    setSuccess("");
  };

  return (
    <Container maxWidth="lg" style={{ height: "100vh", padding: 0 }}>
      {!loggedIn ? (
        <Paper
          elevation={3}
          style={{
            padding: "20px",
            margin: "20px auto",
            maxWidth: "400px",
            textAlign: "center",
          }}
        >
          <Typography variant="h5" gutterBottom>
            Register
          </Typography>
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Mobile"
            fullWidth
            margin="normal"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={register}>
            Register
          </Button>

          <Typography variant="h5" gutterBottom style={{ marginTop: "20px" }}>
            Login
          </Typography>
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={login}>
            Login
          </Button>
        </Paper>
      ) : (
        <Grid container style={{ height: "100vh" }}>
          {/* Sidebar */}
          <Grid
            item
            xs={4}
            style={{
              borderRight: "1px solid #ddd",
              height: "100%",
              overflowY: "auto",
            }}
          >
            <Typography variant="h6" style={{ padding: "16px" }}>
              Chats
            </Typography>
            <Divider />
            <List>
              {users.map((user, index) => (
                <ListItem
                  button
                  key={index}
                  onClick={() => setRecipientEmail(user)}
                >
                  <Avatar style={{ marginRight: "10px" }}>
                    {user[0].toUpperCase()}
                  </Avatar>
                  <ListItemText
                    primary={user}
                    secondary={
                      <Badge color="primary" variant="dot">
                        Online
                      </Badge>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Grid>

          {/* Chat Window */}
          <Grid item xs={8} style={{ display: "flex", flexDirection: "column" }}>
            <Typography variant="h6" style={{ padding: "16px" }}>
              {recipientEmail || "Select a user to start chatting"}
            </Typography>
            <Divider />
            <div
              style={{
                flex: 1,
                overflowY: "auto",
                padding: "16px",
                backgroundColor: "#f5f5f5",
                display: "flex",
                flexDirection: "column",
              }}
            >
              {chat.map((msg, index) => (
                <div
                  key={index}
                  style={{
                    display: "flex",
                    justifyContent:
                      msg.senderEmail === email ? "flex-end" : "flex-start",
                    marginBottom: "8px",
                  }}
                >
                  <Paper
                    elevation={1}
                    style={{
                      padding: "8px 12px",
                      borderRadius: "8px",
                      backgroundColor:
                        msg.senderEmail === email ? "#dcf8c6" : "#fff",
                    }}
                  >
                    <Typography variant="body1">{msg.message}</Typography>
                    <Typography variant="caption" color="textSecondary">
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </Typography>
                  </Paper>
                </div>
              ))}
            </div>
            <div style={{ padding: "16px", borderTop: "1px solid #ddd" }}>
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type a message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={(e) => e.key === "Enter" && sendMessage()}
                InputProps={{
                  endAdornment: (
                    <IconButton color="primary" onClick={sendMessage}>
                      <SendIcon />
                    </IconButton>
                  ),
                }}
              />
            </div>
          </Grid>
        </Grid>
      )}

      {/* Snackbars for error and success messages */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="error">
          {error}
        </Alert>
      </Snackbar>
      <Snackbar open={!!success} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          {success}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
